<h1 class="uk-panel-title"><i class='uk-icon uk-icon-large uk-icon-edit'></i> Elenco elementi</h1><table class='uk-table'>    <thead>    <th>#</th>    <th>Titolo</th>    <th>Categorie</th>    <th>Data creazione</th>    <th>Data ultima modifica</th>    <th>Stato</th>    </thead>    <tbody>    <?php    /** @var \cms\doctrine\model\Article $article */    foreach ($articles as $article) { ?>        <tr>            <td><input type='checkbox' value='<?php echo $article->getId() ?>' name='ids[]'></td>            <td><a href="<?php echo $this->router->link('admin/content/edit/{id}', [$article->getId()]); ?>"><i                            class="uk-icon uk-icon-small uk-icon-hover uk-icon-edit"></i><?php echo $article->getTitle() ?></a>            </td>            <td><ul><?php foreach ($article->getCategories() as $category) ?><li><?php echo $category->getTitle() ?></li></ul></td>            <td><?php if ($article->getDateCreated()) echo $article->getDateCreated()->format('d-m-Y H:i') ?></td>            <td><?php if ($article->getDateUpdated()) echo $article->getDateUpdated()->format('d-m-Y H:i') ?></td>            <td><?php echo $article->getStatus(); ?></td>        </tr>    <?php } ?>    </tbody></table><script>    $('#yx-delete-btn').on('click', function () {        var num = $("input:checked").length;        if (num > 0) {            ids = $('input:checked');            content = "La cancellazione Ã© irreversibile, gli elementi cancellati non potranno essere ripristinati in seguito. Procedere?";            UIkit.modal.confirm(content, function () {                var request = $.ajax({                    url: "<?php echo $this->router->link('admin/content/delete') ?>",                    type: "POST",                    data: ids                });                request.done(function (data) {                    UIkit.modal.confirm("Eliminati " + data + " elementi", function () {                        location.reload();                    });                });            });        } else {            UIkit.modal.alert("Devi selezionare almeno un elemento!");        }    });</script>