<h1 class="uk-panel-title"><i class='uk-icon uk-icon-large uk-icon-edit'></i> Nuovo Banner</h1><form class='uk-form uk-form-stacked uk-width-1-1' action='<?php echo $this->router->link("admin/banner/save"); ?>' method="POST" enctype="multipart/form-data" >    <div class="uk-panel uk-panel-box uk-panel-box-secondary">        <fieldset class='uk-margin-top uk-grid '>            <div class="uk-form-row uk-width-5-10">                <label for="title" class="uk-form-label">Titolo: </label>                <input type="text" id="title" name="title" required="" value="<?php echo isset($banner->title) ? $banner->title :"" ?>">                <label for='state' class="uk-form-label">Stato: </label>                <select id="state" name="status">                    <?php foreach ($states as $key => $state): ?>                        <?php                        $selected = $banner!==null && $banner->status==$key ? "selected" : "";                        ?>                    <option <?php echo $selected ?> value="<?php echo $key?>"><?php echo $state ?></option>                    <?php endforeach;?>                </select>                <label for='type' class="uk-form-label">Tipo: </label>                <select id="type" name='type'>                    <option value="0">Immagine</option>                    <option value="1">Script</option>                </select>            </div>            <div class="uk-form-row uk-width-5-10">                <div id="imginput">                    <?php if (isset($banner) && isset($banner->image)):?>                        <p>Immagine corrente</p>                    <img class="uk-responsive-width uk-width-5-10" src="<?php echo $APP_URL . "public/images/banners/".$banner->image ?>">                    <?php endif; ?>                    <div id="upload-drop" class="uk-placeholder">                        <i class="uk-icon uk-icon-medium uk-icon-file"></i> Sposta qui un file oppure                        <a class="uk-form-file">Scegli un file <input id="upload-select" type="file" name="image"> </a>                    </div>                    <div id="progressbar" class="uk-progress uk-hidden">                        <div class="uk-progress-bar" style="width: 0%;">...</div>                    </div>                    <label class="uk-form-label" for="url">Link</label>                    <input type="text" name="url" value="<?php echo isset($banner->url) ? $banner->url : ""?>">                </div>                <div id="codeinput">                    <label class="uk-form-label" for="code">Script</label>                    <textarea id="code" name="code" value="<?php echo isset($banner->code)? $banner->code : ""?>"></textarea>                </div>            </div>            <label for='user' class="uk-form-label">Cliente: </label>            <select id="user" name='user'>                <?php foreach ($users as $user): ?>                    <?php $selected = $banner->user_id==$user->id ? "selected" : ""; ?>                    <option <?php echo $selected?> value="<?php echo $user->id ?>"><?php echo $user->email ?></option>                <?php endforeach; ?>            </select>        </fieldset>    </div>    </div>    <input type='hidden' value='<?php echo isset($banner->id) ? $banner->id : "" ?>' name='id'></form><script>    $(document).ready(function () {        $('#yx-save-btn').on('click', function () {            if ($('#user').has('option').length >0 ) {                $('form').submit();            } else {                UIkit.notify({                    message : 'Non sono presenti clienti da assegnare al banner. Aggiungi un utente customer',                    status  : 'danger',                    timeout : 5000,                    pos     : 'bottom-right'                });            }        });    });    $("#codeinput").hide();    $("#type").bind("change", function () {        if ($(this).val() == "0") {            $("#imginput").show();            $("#codeinput").hide();        }        else if ($(this).val() == "1") {            $("#imginput").hide();            $("#codeinput").show();        }    });/*    $(function () {        var progressbar = $("#progressbar"),            bar = progressbar.find('.uk-progress-bar'),            settings = {                action: '<?php  echo $this->router->link("admin/banner/upload"); ?>', // upload url                single: true,                allow: '*.(jpg|jpeg|gif|png)', // allow only images                param: "banner",                loadstart: function () {                    bar.css("width", "0%").text("0%");                    progressbar.removeClass("uk-hidden");                },                progress: function (percent) {                    percent = Math.ceil(percent);                    bar.css("width", percent + "%").text(percent + "%");                },                allcomplete: function (response) {                    bar.css("width", "100%").text("100%");                    setTimeout(function () {                        progressbar.addClass("uk-hidden");                    }, 250);                    UIkit.notify({                        message : response,                        status  : 'info',                        timeout : 5000,                        pos     : 'bottom-right'                    });                }            };        var select = UIkit.uploadSelect($("#upload-select"), settings),            drop = UIkit.uploadDrop($("#upload-drop"), settings);    });*/</script>