<h1 class="uk-panel-title"><i class='uk-icon uk-icon-large uk-icon-edit'></i> Nuova Categoria</h1><form class='uk-form uk-form-stacked uk-width-1-1' action='<?php echo $this->router->link('admin/category/save'); ?>'      method="POST">    <div class="uk-panel uk-panel-box uk-panel-box-secondary">        <fieldset>            <div class="uk-form-row">                <label for="title" class="uk-form-label">Titolo: </label>                <input type="text" id="title" name="title" required=""                       value="<?php echo isset($category->title) ? $category->title : "" ?>">            </div>            <div class="uk-form-row">                <label class="uk-form-label" for="editor">Descriziones</label>                <textarea name="description" id="editor" rows="10" cols="50" required=""                          value="<?php echo isset($category->description) ? $category->description : "" ?>">                This is my textarea to be replaced with CKEditor.                    </textarea>                <script>                    CKEDITOR.replace('editor');                    CKEDITOR.config.filebrowserBrowseUrl = '/browse.php';                    CKEDITOR.config.filebrowserUploadUrl = '/upload.php';                </script>            </div>        </fieldset>        <div class='uk-panel-box'>            <fieldset class='uk-margin-top'>                <div class="uk-form-row">                    <label for='meta_description' class="uk-form-label">Metadata description: </label>                    <textarea name='meta_description'                              id="meta_description"><?php echo isset($category->meta_description) ? $category->meta_description : "" ?></textarea>                    <label for='meta_tags' class="uk-form-label">Tags: </label>                    <textarea name='meta_tags'                              id="meta_tags"><?php echo isset($category->meta_tags) ? $category->meta_tags : "" ?></textarea>                </div>            </fieldset>        </div>    </div>    <input type='hidden' value='<?php echo isset($category->id) ? $category->id : "" ?>' name='id'></form><script>    $(document).ready(function () {        $('#yx-save-btn').on('click', function () {            $('form').submit();        });        var content = <?php echo $category->description !== null ? json_encode(htmlspecialchars_decode($category->description)) : '' ?>            CKEDITOR.instances['editor'].setData(content);        $('form').on('submit', function (e) {            var chars = CKEDITOR.instances['editor'].getData().replace(/<[^>]*>/gi, '').length;            var min = 30;        });    });</script>