<?phpnamespace model;use yuxblank\phackp\database\Model;/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of User * * @author yuri.blanc */class User extends Model{    public $id;    public $email;    public $password;    public $role;    public $date_created;    public $status;    public function rolelist()    {        return array(1 => "Cliente", 2 => "Amministratore", 3 => "Super Admin");    }    public function isCustomer()    {        if ($this->role === '1') {            return true;        }    }    public function isAdmin()    {        if ($this->role === '2') {            return true;        }    }    public function isSuperAdmin()    {        if ($this->role === '3') {            return true;        }    }    public function getRole()    {        switch ($this->role) {            case 1 :                return "Cliente";                break;            case 3 :                return "Super Admin";                break;            case 2 :                return "Amministratore";                break;        }    }    public function getStatus()    {        switch ($this->status) {            case 0 :                return "Bloccato";                break;            case 1 :                return "Attivo";                break;        }    }    public function getStates()    {        return array("Bloccato", "Attivo");    }    public function countActive()    {        return $this->count("WHERE status=?", 1);    }    public function countCustomers()    {        return $this->count("WHERE role=?", 1);    }    /**     * Return true if the passed role number is less then or equal the user role     * @param $role     * @return bool     */    public function isAuthorized($role)    {        if ($this->role >= $role) {            return true;        }    }    public function alreadyExist()    {        if ($this->find("WHERE email=?", $this->email)) {            return true;        }    }}