<div class="uk-panel-box uk-margin uk-width-small-1-1 uk-width-medium-1-2  uk-container-center uk-margin-large-top">        <div class="uk-panel-header">            <h3 class="uk-panel-title">Accesso</h3>        </div>    <form class="uk-form uk-margin">            <fieldset class="uk-width-1-6">             <div class="uk-form-row">            <label for ="name" >Email: </label>            <input type="email" class="" name="email" id="name" placeholder="email...">             </div>            <div class="uk-form-row">            <label for="password">Password: </label>            <input type="password" class="uk-form-password" name="password" id="password" placeholder="password...">            </div>            </fieldset>                <button id='loginsubmit' type="sumbit" class="uk-button uk-button-primary uk-margin-top">Login</button>        </form></div>            <script> $('form').on('submit', function(e){    e.preventDefault();var request = $.ajax({  url: "<?php echo $this->router->link('admin/auth') ?>",  type: "POST",  data: $('form').serialize() // dataType: "html"});request.done(function(msg) {  if (msg.trim()==='true') {      window.location.replace("<?php echo $this->router->link('admin')?>");  } else {    UIkit.notify({    message : msg,    status  : 'danger',    timeout : 5000,    pos     : 'top-right'    }); }  });request.fail(function(jqXHR, textStatus) {});    });</script>    